    public class App {
        public void Intro() {
            Console.WriteLine("Hello\nPress any key to continue...");
            Console.ReadKey();
        }
        public int DaysMenu() {
            Console.Clear();
            IDay[] days = new IDay[] {
                new Day1(),
                new Day2(),

                new Day6(),
                };

            Console.WriteLine(string.Format("Choose day (1 - {0}) Q to quit)", days.Length));
            bool valid = false;
            string input;
            int selection = 0;
            for (; !valid; ) {
                input = Console.ReadLine();
                if ("q".Equals(input.ToLower())) return 0;
                int idx;
                if (int.TryParse(input, out idx)) {
                    valid = idx > 0 && idx <= days.Length;
                }

                if (!valid) {
                    Console.Clear();
                    Console.WriteLine(string.Format("Invalid value, try again (1 - {0}): ", days.Length));
                } else {
                    selection = idx - 1;
                }
            }

            IDay day = days[selection];
            Console.Clear();
            Console.WriteLine(day.Name);
            Console.WriteLine("\n");
            day.Print();
            Console.WriteLine("\nPress any key to continue...");
            Console.ReadKey();
            return 1;
        }

    }

    public interface IDay {
        string Name { get; }
        void Print();
    }
